Item.method_code:
var dateField = getFieldByName("date_field");
var textInput = dateField.getElementsByTagName("input")[0];
var buttonInput = dateField.getElementsByTagName("input")[1];

textInput.disabled = !textInput.disabled;
buttonInput.disabled = !buttonInput.disabled;

if (buttonInput.disabled) {
    buttonInput.src = "../images/calendar-disabled.svg";
} else {
    buttonInput.src = "../images/calendar.svg";
}
